Visualization.ToUIElement:
node => {

                StackPanel sp = new StackPanel() { HorizontalAlignment = HorizontalAlignment.Center, VerticalAlignment = VerticalAlignment.Center };
                sp.Orientation = Orientation.Vertical;
                sp.Children.Add(node.Children[1].ToUIElement());
                sp.Children.Add(new Separator());
                sp.Children.Add(node.Children[0].ToUIElement());

                return sp;
                };

node => {
                StackPanel sp = new StackPanel() { HorizontalAlignment = HorizontalAlignment.Center, 
VerticalAlignment = VerticalAlignment.Center };
                sp.Orientation = Orientation.Horizontal;
                sp.Children.Add(node.Children[1].ToUIElement());
                sp.Children.Add(new TextBlock() { Text = " + ", VerticalAlignment = System.Windows.VerticalAlignment.Center } );
                sp.Children.Add(node.Children[0].ToUIElement());

                return sp;
                };
