Visualization.ToUIElement:
node => { StackPanel sp = new StackPanel() { HorizontalAlignment = HorizontalAlignment.Center,  
Margin = new Thickness(0,20,0,0),
VerticalAlignment = VerticalAlignment.Center }; sp.Orientation = Orientation.Horizontal; sp.Children.Add(node.Children[1].ToUIElement()); sp.Children.Add(new TextBlock() { Text = " + ", VerticalAlignment = System.Windows.VerticalAlignment.Center, Margin = new Thickness(0,-20,0,0), } ); sp.Children.Add(node.Children[0].ToUIElement());  return sp; };

